insert into movie (movie_id, name, director, image_path, price, description, media, available_status) values (1, 'James Bond Skyfall', 'Sam Mendes', '/images/skyfall_cover.jpg', 9.99, 'James Bonds (Daniel Craig) Loyalität zu M (Judi Dench) wird auf eine harte Probe gestellt, als die Chefin des MI6 von ihrer Vergangenheit eingeholt wird. Nachdem Bonds letzte Mission fehlgeschlagen ist, befinden sich Agenten auf der ganzen Welt in Lebensgefahr und auch der MI6 selbst wird angegriffen. Von Feinden umgeben hat M nur noch einen Verbündeten, dem sie trauen kann: 007, der die Angreifer aufspüren und unschädlich machen muss. Das kriminelle Genie hinter den Angriffen, Silva (Javier Bardem), macht jedoch vor nichts Halt um seine Rachegelüste zu stillen.', 'BLURAY', true);
