insert into movie (movie_id, rating, year, name, director, image_path, price, description, media, on_stock, available_status) values (1, 3.8, '2016', 'James Bond 007 - Skyfall', 'Sam Mendes', 'images/skyfall_cover.jpg', 9.99, 'James Bonds (Daniel Craig) Loyalität zu M (Judi Dench) wird auf eine harte Probe gestellt, als die Chefin des MI6 von ihrer Vergangenheit eingeholt wird. Nachdem Bonds letzte Mission fehlgeschlagen ist, befinden sich Agenten auf der ganzen Welt in Lebensgefahr und auch der MI6 selbst wird angegriffen. Von Feinden umgeben hat M nur noch einen Verbündeten, dem sie trauen kann: 007, der die Angreifer aufspüren und unschädlich machen muss. Das kriminelle Genie hinter den Angriffen, Silva (Javier Bardem), macht jedoch vor nichts Halt um seine Rachegelüste zu stillen.', 'BLURAY', 10, true);
insert into movie (movie_id, rating, year, name, director, image_path, price, description, media, on_stock, available_status) values (2, 4.8, '2006', 'Prestige', 'Christopher Nolan', 'images/prestige_cover.jpg', 4.99, 'Als sich das zwanzigste Jahrhundert ankündigt, befindet sich das viktorianische England im Aufbruch. Kunst und Unterhaltung stehen hoch im Kurs und damit auch die Magier, die beides in ihren Shows vereinigen. Zwei Zauberkünstler setzen in dieser Zeit neue Maßstäbe. Alfred Borden ist ein wahres Genie, weiß seine ausgeklügelten Tricks aber nicht recht in Szene zu setzen. Robert Angier hingegen ist ein Entertainer erster Güte. Am Anfang gehen die beiden Nachwuchstalente ein Stück gemeinsam. Aber ein traumatisches Ereignis sorgt dafür, dass Alfred und Robert zu erbitterten Gegnern werden. Fortan will jeder dem anderen die Show stehlen. Sich gegenseitig aufschaukelnd, geraten die Magier außer Rand und Band, wobei Menschenleben nichts mehr zählen.', 'BLURAY', 12, true);
insert into movie (movie_id, rating, year, name, director, image_path, price, description, media, on_stock, available_status) values (3, 4.0, '2014', 'Synecdoche New York', 'Charlie Kaufman', 'images/synecdoche_cover.jpg', 6.76, 'Der Theaterregisseur Caden Cotard lebt in der Kleinstadt Schenectady im Bundesstaat New York. Als er eines Tages ein seltsames Augenleiden feststellt, ist dies der Auftakt zu einer Serie von mysteriösen Nervenkrankheiten, die seine Körperfunktionen außer Kraft zu setzen scheinen. Dann verlässt ihn auch noch Ehefrau Adele mitsamt der gemeinsamen Tochter Olive in Richtung Berlin, wo sie eine hippe Künstlerexistenz beginnt. Seine neue Beziehung mit der sexy Kassiererin Hazel ist zu Ende, ehe sie richtig begonnen hat. Cadens Leben ist offenbar völlig aus den Fugen. Irgendetwas muss geschehen! Also mietet er sich für das neue Stück seiner Theatergruppe ein riesiges leerstehendes ehemaliges Kaufhaus mitten in New York. Nach und nach bastelt er darin eine Nachbildung der realen Welt mit Schauspielern, die darin ein fiktionales Leben leben. Er engagiert weitere Schauspieler, die ihn selbst und sein gesamtes Umfeld verkörpern sollen. Die Jahre ziehen ins Land, die Stadt in der Stadt wächst und wächst und mittendrin verliert sich Caden in einem Dschungel aus Realität und Fiktion...', 'BLURAY', 4, true);
insert into movie (movie_id, rating, year, name, director, image_path, price, description, media, on_stock, available_status) values (4, 4.7, '2008', 'Blue Valentine', 'Derek Cianfrance', 'images/blue_valentine_cover.jpg', 7.99, 'Nach sechs gemeinsamen Jahren ist Deans und Cindys Ehe am Ende. Was als romantische Leidenschaft mit unbedingter Hingabe begann, ist schleichender Ernüchterung gewichen: Aus Liebeserklärungen per Ukulele und Stepptanz wurden banale Streits um Geld, um fehlgeschlagene Ambitionen und um Tochter Frankie. Wie es so weit kommen konnte, weiß keiner von ihnen. Den Kampf scheinen beide bereits verloren zu haben. Als letzten Rettungsversuch für ihre Ehe verbringen Dean und Cindy eine Nacht in einem Motel, in der sowohl zärtliche Erinnerungen an die erste Zeit als Paar als auch die brutale Gewissheit wach werden, dass sie vor der härtesten Veränderung ihres Lebens stehen.', 'DVD', 15, true);
insert into movie (movie_id, rating, year, name, director, image_path, price, description, media, on_stock, available_status) values (5, 4.2, '2014', 'Grand Budapest Hotel', 'Wes Anderson', 'images/budapest_cover.jpg', 6.81, 'GRAND BUDAPEST HOTEL erzählt die abenteuerliche Geschichte von Gustave H. (Ralph Fiennes), dem legendären Hotelconcierge eines berühmten europäischen Hotels, und seinem Protegé, dem Hotelpagen Zero Moustafa (Tony Revolori).Beide werden enge Freunde als sie in den Streit um ein großes Familienvermögen und den Diebstahl eines wertvollen Renaissance Gemäldes verwickelt werden. All das passiert zu einer Zeit, in der Kriege ausbrechen und Europa sich vollkommen verändert…', 'DVD', 2, true);
insert into movie (movie_id, rating, year, name, director, image_path, price, description, media, on_stock, available_status) values (6, 4.9, '2015', 'Drive', 'Nicolas Winding Refn', 'images/drive_cover.jpg', 7.79, 'Tagsüber arbeitet Driver (Ryan Gosling) als Stuntman für Hollywood. Reine Routine. Erst nachts erwacht der wortkarge Einzelgänger zu Leben, als Fahrer von Fluchtfahrzeugen bewaffneter Einbrüche. Keiner kann ihn schnappen, keiner kann ihm das Wasser reichen. Dann lernt der coole Driver seine neue Nachbarin Irene (Carey Mulligan) kennen - und verliebt sich in die alleinerziehende Mutter. Als Irenes Ehemann Standard (Oscar Isaac) aus dem Knast entlassen wird, lässt sich Driver zu einem vermeintlich todsicheren Ding überreden: Mit der erbeuteten Kohle will Standard seine Schulden abbezahlen und Irenes eine gesicherte Zukunft bieten. Doch alles geht schief. Die Jagd auf Driver und Irene ist eröffnet - wenn es ihm nicht sofort gelingt, den Spieß umzudrehen...', 'DVD', 44, true);
